import{_ as d,o,c as e,ag as r}from"./chunks/framework.CbQjVMS6.js";const a="/videos/relationship-graph.mp4",p=JSON.parse('{"title":"关系图","description":"","frontmatter":{},"headers":[],"relativePath":"tutorial/relationship-graph.md","filePath":"tutorial/relationship-graph.md","lastUpdated":1770122747000}'),c={name:"tutorial/relationship-graph.md"};function n(s,t,h,i,g,l){return o(),e("div",null,[...t[0]||(t[0]=[r('<h1 id="关系图" tabindex="-1">关系图 <a class="header-anchor" href="#关系图" aria-label="Permalink to &quot;关系图&quot;">​</a></h1><p>这里介绍关系图的节点操作、连线配置等功能。</p><h2 id="视频教程" tabindex="-1">视频教程 <a class="header-anchor" href="#视频教程" aria-label="Permalink to &quot;视频教程&quot;">​</a></h2><video controls width="100%" preload="metadata"><source src="'+a+'" type="video/mp4"> 您的浏览器不支持 video 标签。 </video><h2 id="自动解析规则" tabindex="-1">自动解析规则 <a class="header-anchor" href="#自动解析规则" aria-label="Permalink to &quot;自动解析规则&quot;">​</a></h2><p>关系图会自动扫描符合以下命名规则的表格数据：</p><h3 id="_1-表格识别" tabindex="-1">1. 表格识别 <a class="header-anchor" href="#_1-表格识别" aria-label="Permalink to &quot;1. 表格识别&quot;">​</a></h3><table tabindex="0"><thead><tr><th>表格类型</th><th>表名关键词（包含其一即可）</th><th>必需列名（包含其一即可）</th></tr></thead><tbody><tr><td><strong>人物表</strong></td><td><code>人物</code>, <code>角色</code>, <code>npc</code>, <code>character</code>, <code>主角</code></td><td><code>姓名</code>, <code>name</code>, <code>名称</code>, <code>角色名</code></td></tr><tr><td><strong>关系表</strong></td><td><code>关系</code>, <code>relationship</code>, <code>relation</code></td><td><code>source/源</code>, <code>target/目标</code></td></tr><tr><td><strong>势力表</strong></td><td><code>势力</code>, <code>faction</code>, <code>组织</code>, <code>阵营</code></td><td><code>名称</code>, <code>name</code>, <code>势力名</code></td></tr></tbody></table><h3 id="_2-列名识别" tabindex="-1">2. 列名识别 <a class="header-anchor" href="#_2-列名识别" aria-label="Permalink to &quot;2. 列名识别&quot;">​</a></h3><table tabindex="0"><thead><tr><th>数据类型</th><th>对应列名关键词（包含其一即可）</th><th>说明</th></tr></thead><tbody><tr><td><strong>人际关系</strong></td><td><code>人际关系</code>, <code>关系</code>, <code>relationship</code></td><td>位于人物表中，用于解析内嵌关系</td></tr><tr><td><strong>势力归属</strong></td><td><code>所属</code>, <code>势力</code>, <code>阵营</code>, <code>身份</code>, <code>职业</code></td><td>位于人物表中，用于解析角色所属势力</td></tr><tr><td><strong>源节点</strong></td><td><code>source</code>, <code>源</code>, <code>主体</code>, <code>from</code></td><td>位于关系表中</td></tr><tr><td><strong>目标节点</strong></td><td><code>target</code>, <code>目标</code>, <code>客体</code>, <code>to</code></td><td>位于关系表中</td></tr><tr><td><strong>关系词</strong></td><td><code>关系</code>, <code>relation</code>, <code>关系词</code></td><td>位于关系表中</td></tr><tr><td><strong>备注</strong></td><td><code>备注</code>, <code>note</code>, <code>说明</code></td><td>位于关系表中</td></tr></tbody></table><h2 id="匹配规则自查" tabindex="-1">匹配规则自查 <a class="header-anchor" href="#匹配规则自查" aria-label="Permalink to &quot;匹配规则自查&quot;">​</a></h2><p>如果你的关系解析不正确，请检查是否符合以下格式：</p><p><strong>通用规则</strong>（在人物表的“人际关系”列或势力表中均可使用）：</p><table tabindex="0"><thead><tr><th>模式</th><th>格式</th><th>示例</th><th>解析结果</th></tr></thead><tbody><tr><td><strong>标准格式</strong></td><td><code>{目标}的{关系}</code></td><td>小红的哥哥</td><td>目标：小红，关系：哥哥</td></tr><tr><td><strong>多目标</strong></td><td><code>{A}、{B}的{关系}</code></td><td>小明、小红的朋友</td><td>目标：[小明, 小红]，关系：朋友</td></tr><tr><td><strong>多关系</strong></td><td><code>{目标}的{关系A}、{关系B}</code></td><td>小红的朋友、同学</td><td>目标：小红，关系：[朋友, 同学]</td></tr><tr><td><strong>混合模式</strong></td><td><code>{目标}：{关系A}、{关系B}</code></td><td>小红：朋友、同学</td><td>目标：小红，关系：[朋友, 同学]</td></tr><tr><td><strong>双向模式</strong></td><td><code>{A}与{B}的{关系}</code></td><td>我与小红的朋友</td><td>目标：[我, 小红]，关系：朋友</td></tr><tr><td><strong>备注模式</strong></td><td><code>{目标}的{关系}（{备注}）</code></td><td>小明的死对头（暗中）</td><td>目标：小明，关系：死对头，备注：暗中</td></tr><tr><td><strong>势力关系</strong></td><td><code>与{势力}{关系}</code></td><td>与胜和敌对</td><td>目标：胜和，关系：敌对</td></tr><tr><td><strong>冒号格式</strong></td><td><code>{目标}：{关系}</code></td><td>藏剑山庄：盟友</td><td>目标：藏剑山庄，关系：盟友</td></tr><tr><td><strong>反向冒号</strong></td><td><code>{关系}：{目标}</code></td><td>盟友：藏剑山庄</td><td>目标：藏剑山庄，关系：盟友（需开启智能识别）</td></tr></tbody></table><h2 id="关系类型与颜色" tabindex="-1">关系类型与颜色 <a class="header-anchor" href="#关系类型与颜色" aria-label="Permalink to &quot;关系类型与颜色&quot;">​</a></h2><p>关系图会自动根据关系词判断关系类型，并赋予相应的颜色。优先级：S &gt; A &gt; E &gt; D &gt; B &gt; C</p><table tabindex="0"><thead><tr><th>类型</th><th>级别</th><th>颜色</th><th>匹配关键词</th></tr></thead><tbody><tr><td>💗 <strong>爱/沉迷</strong></td><td>S</td><td>粉红 #E91E63</td><td>恋, 男友, 女友, 爱, 暧昧, 情人, 伴侣, 老婆, 老公, 丈夫, 妻子, 粉丝, 迷, 倾心, 心上人</td></tr><tr><td>💙 <strong>亲属</strong></td><td>A</td><td>蓝 #2196F3</td><td>父, 母, 爹, 娘, 妈, 爸, 兄, 哥, 弟, 姐, 妹, 爷, 奶, 叔, 姑, 舅, 姨, 亲, 族, 祖, 儿, 女, 孙</td></tr><tr><td>💚 <strong>师/友/同伴</strong></td><td>B</td><td>绿 #4CAF50</td><td>友, 盟, 知己, 故交, 青梅竹马, 发小, 闺蜜, 死党, 伙, 同窗, 师, 弟子, 学生, 队友, 搭档, 同伴, 同门, 徒, 教官, 学徒, 向导, 哨兵</td></tr><tr><td>⚪ <strong>社会关系</strong></td><td>C</td><td>灰 #9E9E9E</td><td>同事, 邻居, 舍友, 室友, 顾客, 客户, 上司, 下属, 同学, 路人, 熟人, 认识, 相识, 同乡, 老乡, 房东, 租客, 店员, 老板, 员工, 主管, 经理, 助手, 秘书, 管家, 仆人, 侍从, 护卫, 保镖</td></tr><tr><td>🟡 <strong>利益关系</strong></td><td>D</td><td>橙 #FF9800</td><td>合作, 雇佣, 交易, 债务, 炮友, 利用, 工具人, 棋子, 操控, 傀儡, 买卖, 债主, 欠债, 商业, 合伙, 投资, 赞助, 金主, 资助, 包养, 契约, 协议</td></tr><tr><td>🔴 <strong>敌对关系</strong></td><td>E</td><td>红 #F44336</td><td>仇, 敌, 死, 对手, 竞争, 仇敌, 死敌, 宿敌, 威胁, 敌对, 仇人, 仇家, 敌人, 对头, 冤家, 杀父, 杀母, 血仇, 深仇, 世仇, 夙敌, 劲敌, 强敌, 死对头, 眼中钉, 宿主, 夺舍, 寄生</td></tr><tr><td>🟣 <strong>复杂/未知</strong></td><td>?</td><td>紫 #9C27B0</td><td>当正面关系(S/A/B)和敌对关系(E)同时存在时自动判定</td></tr></tbody></table><h3 id="线条样式" tabindex="-1">线条样式 <a class="header-anchor" href="#线条样式" aria-label="Permalink to &quot;线条样式&quot;">​</a></h3><table tabindex="0"><thead><tr><th>样式</th><th>说明</th><th>触发关键词</th></tr></thead><tbody><tr><td><strong>实线</strong></td><td>默认样式，表示当前直接的关系</td><td>-</td></tr><tr><td><strong>虚线</strong> ┄</td><td>过往/隐藏关系</td><td>过去, 过往, 从前, 曾经, 曾, 前, 原, 故, 昔, 已故, 已逝, 前任, 旧, 暗, 秘, 隐, 伪, 假, 表面, 疑, 可能, 潜在, 疑似</td></tr></tbody></table>',19)])])}const u=d(c,[["render",n]]);export{p as __pageData,u as default};
